// logoMaker.js

const readline = require('readline');
const fs = require('fs');
const svgCaptcha = require('svg-captcha');

const rl = readline.createInterface({
    input: process.stdin,
    output: process.stdout
});

function generateLogo() {
    rl.question("Enter up to three characters for the logo: ", (text) => {
        rl.question("Enter text color (keyword or hexadecimal): ", (textColor) => {
            console.log("Choose a shape:");
            console.log("1. Circle");
            console.log("2. Triangle");
            console.log("3. Square");

            rl.question("", (shapeChoice) => {
                rl.question("Enter shape color (keyword or hexadecimal): ", (shapeColor) => {
                    // Generate SVG captcha with the entered text
                    const svgOptions = {
                        size: { width: 300, height: 200 },
                        background: shapeColor,
                        color: textColor,
                        noise: 0
                    };

                    const captcha = svgCaptcha.create(svgOptions);
                    
                    // Write SVG content to file
                    const svgFileName = 'logo.svg';
                    fs.writeFileSync(svgFileName, captcha.data);
                    console.log(`Generated ${svgFileName}`);

                    // Close the readline interface
                    rl.close();
                });
            });
        });
    });
}

// Call the function to generate the logo
generateLogo();